mapper.insert:
insert into crowdfund_comment (ID, TOPIC_ID, TOPIC_TYPE, 
  CONTENT, FROM_UID, COMMENT_DATE, 
  STATUS, AUDIT_ID, AUDIT_DATE
  )
values (#{id,jdbcType=INTEGER}, #{topicId,jdbcType=INTEGER}, #{topicType,jdbcType=VARCHAR}, 
  #{content,jdbcType=VARCHAR}, #{fromUid,jdbcType=INTEGER}, #{commentDate,jdbcType=TIMESTAMP}, 
  #{status,jdbcType=BIT}, #{auditId,jdbcType=INTEGER}, #{auditDate,jdbcType=TINYINT}
  )

mapper.select:
SELECT COUNT(*) FROM crowdfund_comment CC,crowdfund_basicinfo CB
WHERE CC.TOPIC_ID=CB.PROJECT_NO
AND CC.TOPIC_ID=#{topicId,jdbcType=INTEGER}

SELECT PROJECT_NO,PROJECT_NAME,PROJECT_SYNOPSIS,PROJECT_IMAGE
FROM crowdfund_comment CC,crowdfund_basicinfo CB
WHERE CC.TOPIC_ID=CB.PROJECT_NO
AND CC.TOPIC_ID=#{topicId,jdbcType=INTEGER}
limit #{page},#{rows}
